function calculateTotalTimeDifferenceForMonth(data, month) {
    const filteredData = data.filter(dataPoint => {
        const dataPointDate = new Date(dataPoint.time);
        return dataPointDate.getMonth() + 1 === month;
    });

    if (filteredData.length === 0) {
        return { hours: 0, minutes: 0 };
    }

    const startTimestamp = new Date(filteredData[0].time);
    const endTimestamp = new Date(filteredData[filteredData.length - 1].time);
    // @ts-ignore
    const timeDifferenceInMilliseconds = endTimestamp - startTimestamp;

    const hours = Math.floor(timeDifferenceInMilliseconds / (1000 * 60 * 60));
    const minutes = Math.floor((timeDifferenceInMilliseconds % (1000 * 60 * 60)) / (1000 * 60));

    return { hours, minutes };
}

